mapper.insert:
insert into goods (  )
values(#{goodsNum}, #{goodsName}, #{goodsPrice}, #{goodsContents}
		,#{empNum}, sysdate, null, null,null,null,0,null,null)

mapper.select:
select * from ( select ROW_NUMBER() over (order by goods_num desc) as rn,
from goods
goods_name like '%' || #{searchWord} || '%' or goods_num like '%' || #{searchWord} || '%'
)sub1 where rn between #{startRow} and #{endRow}

mapper.delete:
delete from goods 
where goods_num in 
						#{goodsNum}

mapper.update:
 
update goods
set GOODS_NAME = #{goodsName}, GOODS_PRICE = #{goodsPrice}
	,GOODS_CONTENTs = #{goodsContents}
    ,update_emp_num = #{updateEmpNum}, goods_update_date = sysdate
where goods_num = #{goodsNum}

update goods
set visit_count = visit_count + 1
where goods_num = #{goodsNum}
