mapper.select:
select inquire_num, i.member_num, i.goods_num, inquire_subject, inquire_contents ,inquire_kind, inquire_date, inquire_answer, inquire_answer_date ,i.emp_num ,member_id ,goods_name from inquire i left outer join members m on i.member_num = m.member_num join goods g on i.goods_num = g.goods_num
i.goods_num = #{goodsNum}
and inquire_num = #{inquireNum}

mapper.update:
update inquire
set INQUIRE_SUBJECT = #{inquireSubject}
   ,INQUIRE_CONTENTS = #{inquireContents}
   ,INQUIRE_KIND = #{inquireKind}
where  INQUIRE_NUM = #{inquireNum}
